INSERT INTO `lp_question` VALUES ('7', '7', '判断题', '《消费者权益保护法》规定，消费者向有关行政部门投诉的，该部门应当自收到投诉之日起七日内，予以处理并告知消费者。', '', '0', '0', '00010001', '0');
