INSERT INTO `lp_question` VALUES ('20', '20', '判断题', '各级人民政府及其有关部门应当采取多种形式，加强对有关安全生产的法律、法规和安全生产知识的宣传，增强全社会的安全生产意识。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('21', '21', '判断题', '生产经营单位应当具备的安全生产条件所必需的资金投入，由生产经营单位的决策机构，主要负责人或者个人经营的投资人予以保证，并对由于安全生产所必需的资金投入不足导致的后果承担责任。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('22', '22', '判断题', '生产经营单位生产、经营、运输、储存、使用危险物品或者处置废弃危险物品，必须执行有关法律、法规和国家标准或者行业标准，建立专门的安全管理制度，采取可靠的安全措施，接受有关主管部门依法实施的监督管理。', '', '0', '0', '00010001', '0');
