INSERT INTO `lp_question` VALUES ('135', '135', '判断题', '经行政复议的案件，复议机关改变原具体行政行为的，也可以由复议机关所在地人民法院管辖。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('136', '136', '判断题', '在行政诉讼中，当事人不服人民法院第一审判决的，有权在判决书送达之日起15日内向上一级人民法院提出上诉。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('137', '137', '判断题', '行政许可直接涉及申请人与他人之间重大利益关系的，行政机关在作出行政许可决定前，应当告知申请人、利害关系人享有要求听证的权利；申请人、利害关系人在被告知听证权利之日起5日内提出听证申请的，行政机关应当在20日内组织听证。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('138', '138', '判断题', '《行政许可法》适用于有关行政机关对其他行政机关或者对其直接管理的事业单位的人事、财务、外事等事项的审批。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('139', '139', '判断题', '行政机关实施行政许可，依照法律、行政法规收取费用的，应当按照公布的法定项目和标准收费；所收取的费用必须全部上缴国库，任何机关或者个人不得以任何形式截留、挪用、私分或者变相私分。财政部门不得以任何形式向行政机关返还或者变相返还实施行政许可所收取的费用。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('140', '140', '判断题', '被许可人以欺骗、贿赂等不正当手段取得行政许可的，可以予以撤销。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('141', '141', '判断题', '《政府采购法》规定，在政府采购活动中，采购人员及相关人员与供应商有利害关系的，应当回避。供应商认为采购人员及相关人员与其他供应商有利害关系的，可以申请其回避。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('142', '142', '判断题', '《行政诉讼法》规定，因不动产提起的行政诉讼，由原告或被告住所地人民法院管辖。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('143', '143', '判断题', '《行政诉讼法》规定，行政诉讼中两个以上人民法院都有管辖权的案件，原告可以选择其中一个人民法院提起诉讼。原告向两个以上有管辖权的人民法院提起诉讼的，由最先立案的人民法管辖。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('144', '144', '判断题', '《行政诉讼法》规定，因不动产提起诉讼的案件从行政行为作出之日起超过二十年，其他案件从行政行为作出之日起超过五年提起诉讼的，人民法院不予受理。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('145', '145', '判断题', '有权申请行政复议的公民死亡的，其他人无权代为申请行政复议。有权申请行政复议的公民为无民事行为能力人或者限制民事行为能力人的，其法定代理人可以代为申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('146', '146', '判断题', '对地方各级人民政府的具体行政行为不服的，可以向上一级地方人民政府申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('147', '147', '判断题', '在行政复议过程中，被申请人可以自行向申请人和其他有关组织或者个人收集证据。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('148', '148', '判断题', '行政复议申请人应当以书面形式申请行政复议，不能以口头形式申请。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('149', '149', '判断题', '情况复杂，不能在规定期限内作出行政复议决定的，经行政复议机关负责人批准，可以适当延长行政复议期限，但延长期限最多不超过30日。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('150', '150', '判断题', '公民认为行政机关不依法履行法定职责的，不能申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('151', '151', '判断题', '公务员不服人事处理决定的，不能申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('152', '152', '判断题', '公民、法人或者其他组织向人民法院提起行政诉讼，人民法院已经依法受理的，不得申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('153', '153', '判断题', '行政复议机关应当审查申请行政复议的具体行政行为的合法性与适当性。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('154', '154', '判断题', '行政强制措施由法律、法规规定的行政机关在法定职权范围内实施。行政强制措施权不得委托。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('155', '155', '判断题', '被申请人不提交当初作出具体行政行为的证件、依据和其他有关材料的，行政复议机关可以撤销原具体行政行为。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('156', '156', '判断题', '不服行政机关对民事纠纷作出的调解的，可以申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('157', '157', '判断题', '发展社会主义民主政治，最根本的是坚持党的领导、人民当家作主和依法治国的有机统一。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('158', '158', '判断题', '行政机关作出强制执行决定前，应当事先催告当事人履行义务。催告应当以书面形式作出。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('159', '159', '判断题', '《行政强制法》规定，划拨存款、汇款应当由法律规定的行政机关决定，并书面通知金融机构。金融机构接到行政机关依法作出划拨存款、汇款的决定后，应当立即划拨。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('160', '160', '判断题', '在行政诉讼中，人民法院如果发现行政法规和行政规章与宪法、法律相抵触，可以撤销。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('161', '161', '判断题', '行政机关应当于举行听证的七日前将举行听证的时间、地点通知申请人、利害关系人，必要时予以公告。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('162', '162', '判断题', '依法拍卖财物，由行政机关委托拍卖机构依照《中华人民共和国拍卖法》的规定办理。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('163', '163', '判断题', '具体行政行为认定事实不清的，证据不足的，行政复议机关可以调查补正后作出维持决定。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('164', '164', '判断题', '行政机关工作人员办理行政许可、实施监督检查，索取或者收受他人财物或者谋取其他利益，构成犯罪的，依法追究刑事责任。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('165', '165', '判断题', '行政机关依法作出不予行政许可的书面决定的，可以不说明理由，但要告知申请人享有依法申请行政复议或者提起行政诉讼的权利。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('166', '166', '判断题', '行政机关在调查或者进行检查时，执法人员不得少于两人，并应当向当事人或者有关人员出示证件。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('167', '167', '判断题', '行政复议机关受理行政复议申请，可以向申请人收取一定数额的行政复议费用。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('168', '168', '判断题', '在行政诉讼中，作为被告的行政机关法定代表人可以出庭参加诉讼，也可以委托代理人参加诉讼。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('169', '169', '判断题', '行政诉讼中的第三人只能通过申请参加到行政诉讼中来。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('170', '170', '判断题', '作出罚款决定的行政机关应当与收缴罚款的机构分离。作出行政处罚决定的行政机关及其执法人员在任何情况下均不得自行收缴罚款。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('171', '171', '判断题', '限制人身自由的行政处罚权即行政拘留的决定权只能由公安机关行使。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('172', '172', '判断题', '国家为了公共利益的需要，可以依照法律规定对公民的私有财产实行征收或者征用并给予补偿。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('173', '173', '判断题', '行政机关申请人民法院强制执行，不缴纳申请费。强制执行的费用由被执行人承担。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('174', '174', '判断题', '行政机关依申请提供政府信息，除可以收取检索、复制、邮寄等成本费用外，不得收取其他费用。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('175', '175', '判断题', '行政诉讼法规定，当事人对已发生法律效力的判决、裁定，认为确有错误的，可以向原审人民法院或者上一级人民法院提出申诉，判决、裁定停止执行。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('176', '176', '判断题', '对县级以上地方各级人民政府工作部门的具体行政行为不服的，由申请人选择，可以向该部门的本级人民政府申请行政复议，也可以向上一级主管部门申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('177', '177', '判断题', '被告行政机关委托的律师可以向原告和证人收集证据。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('178', '178', '判断题', '公民、法人或者其他组织认为行政机关和行政机关工作人员的具体行政行为侵犯其合法权益，有权依照《行政诉讼法》向人民法院提起诉讼。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('179', '179', '判断题', '行政复议既审查行政行为的合法性，又审查行政行为的合理性。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('180', '180', '判断题', '法律、行政法规设定的行政许可，其适用范围没有地域限制的，申请人取得的行政许可在全国范围内有效。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('181', '181', '判断题', '行政诉讼被告负举证责任，但不排除对某些事项应当由原告提供证据。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('182', '182', '判断题', '行政处罚由被处罚人所在地的县级以上地方人民政府具有行政处罚权的行政机关管辖。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('183', '183', '判断题', '凡是侵犯公民、法人和其他组织合法权益的具体行政行为，均属于行政复议的受理范围。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('184', '184', '判断题', '行政相对人对规章以下的规定不服，可就该规定单独申请行政复议。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('185', '185', '判断题', '行政机关实施行政许可，不得收取任何费用，但行政机关提供行政许可申请书格式文本的除外。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('186', '186', '判断题', '行政行为的撤销是因行政机关的过错引起，并给行政相对人造成损害的，则行政机关应负赔偿责任。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('187', '187', '判断题', '行政诉讼不适用调解是指对诉讼中任何问题均不得进行调解。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('188', '188', '判断题', '谁主张、谁举证是举证责任分配的一般原则，行政诉讼也不例外。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('189', '189', '判断题', '行政机关发现自己作出的行政行为有错可以依法自行撤销或改变行政行为，但有些具体行政行为的撤销或变更给相对人造成损失的，应当依法赔偿。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('190', '190', '判断题', '罚款主要适用于以牟取非法经济利益为目的的行政违法行为。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('191', '191', '判断题', '在行政复议期间，申请人认为需要停止执行的具体行政行为可以停止执行。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('192', '192', '判断题', '公安干警在下班期间制止犯罪的行为是公务行为。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('193', '193', '判断题', '内部行政行为可以适用行政申诉，但不得提起行政诉讼。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('194', '194', '判断题', '法律没有规定行政机关强制执行的，作出行政决定的行政机关应当申请人民法院强制执行。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('195', '195', '判断题', '并非所有的行政行为都有强制执行的效力。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('196', '196', '判断题', '设定和实施行政处罚必须以事实为依据，与违法行为的事实、性质、情节以及社会危害程度相当。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('197', '197', '判断题', '对违法行为给予行政处罚的规定必须公布；未经公布的，不得作为行政处罚的依据。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('198', '198', '判断题', '行政机关无权撤销自己作出的行政行为。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('199', '199', '判断题', '违法行为在两年内未被发现的，不再给予行政处罚。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('200', '200', '判断题', '如当事人有申辩行为，行政机关可因其认错态度而加重处罚。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('201', '201', '判断题', '行政处罚适用简易程序时可对违法行为当场进行处罚。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('202', '202', '判断题', '行政机关采取行政强制措施，必须有法律、法规的明确授权，并严格依照法律规定实施。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('203', '203', '判断题', '未依法说明不受理行政许可申请或者不予行政许可的理由的，由其上级行政机关或者监察机关责令改正；情节严重的，对直接负责的主管人员和其他直接责任人员依法给予行政处分。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('204', '204', '判断题', '行政机关及其执法人员当场收缴罚款的，必须向当事人出具省、自治区、直辖市财政部门统一制发的罚款收据；不出具财政部门统一制发的罚款收据的，当事人有权拒绝缴纳罚款。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('205', '205', '判断题', '行政机关为牟取本单位私利，对应当依法移交司法机关追究刑事责任的不移交，以行政处罚代替刑罚，由上级行政机关或者有关部门责令纠正；拒不纠正的，对直接负责的主管人员给予行政处分；徇私舞弊、包庇纵容违法行为的，按照刑法相关规定处理。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('206', '206', '判断题', '当事人确有经济困难，需要延期或者分期缴纳罚款的，经当事人申请和行政机关批准，应当暂缓或者分期缴纳。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('207', '207', '判断题', '只要当事人对行政处罚决定不服申请行政复议或者提起行政诉讼的，行政处罚一律停止执行。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('208', '208', '判断题', '被申请人不履行或者无正当理由拖延履行行政复议决定的，对直接负责的主管人员和其他直接责任人员依法给予警告、记过、记大过的行政处分；经责令履行仍拒不履行的，依法给予降级、撤职、开除的行政处分。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('209', '209', '判断题', '行政复议机构中止、恢复行政复议案件的审理，可以告知有关当事人。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('210', '210', '判断题', '公民、法人或者其他组织对行政机关行使法律、法规规定的自由裁量权作出的具体行政行为不服申请行政复议的，行政复议机关可以按照自愿、合法的原则进行调解。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('211', '211', '判断题', '赔偿义务机关赔偿损失后，应当责令有过失的工作人员或者受委托的组织或者个人承担部分或者全部赔偿费用。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('212', '212', '判断题', '赔偿请求人可以向共同赔偿义务机关中的任何一个赔偿义务机关要求赔偿，该赔偿义务机关应当先予赔偿。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('213', '213', '判断题', '赔偿请求人根据受到的不同损害，不可以同时提出数项赔偿要求。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('214', '214', '判断题', '经复议机关复议的，最初造成侵权行为的行政机关为赔偿义务机关，但复议机关的复议决定加重损害的，复议机关对加重的部分履行赔偿义务。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('215', '215', '判断题', '赔偿请求人请求国家赔偿的时效为3年，自国家机关及其工作人员行使职权时的行为被依法确认为违法之日起计算，但被羁押期间不计算在内。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('216', '216', '判断题', '经上级行政机关复议的行政案件，复议机关改变原具体的行政行为的，也可由复议机关所在地的人民法院管辖。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('217', '217', '判断题', '《行政诉讼法》规定，当事人一方或者双方为二人以上，因同一行政行为发生的行政案件，或者因同类行政行为发生的行政案件,人民法院认为可以合并审理的，为共同诉讼。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('218', '218', '判断题', '行政诉讼中，没有诉讼行为能力的公民，由其法定代理人代为诉讼。法定代理人互相推诿代理责任的，由人民法院指定其中一人代为诉讼。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('219', '219', '判断题', '在行政诉讼中，原告对作出的具体行政行为负有举证责任，被告应当提供作出该具体行政行为的证据和所依据的规范性文件。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('220', '220', '判断题', '原告可以提供证明具体行政行为违法的证据。原告提供的证据不成立的，可以免除被告的举证责任。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('221', '221', '判断题', '人民法院审理行政案件，参照规章；认为规章之间不一致的，由最高人民法院送请国务院作出判决。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('222', '222', '判断题', '适用简易程序审理的行政案件，由审判员一人独任审理。', '', '0', '0', '00010001', '0');
