INSERT INTO `lp_question` VALUES ('227', '227', '判断题', '发展循环经济应当在技术可行、经济合理和有利于节约资源、保护环境的前提下，按照资源化优先的原则实施。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('228', '228', '判断题', '生产列入强制回收名录的产品或者包装物的企业，必须对废弃的产品或者包装物负责回收。', '', '0', '0', '00010001', '0');
