INSERT INTO `lp_question` VALUES ('230', '230', '判断题', '从事工艺、设备、产品及包装物设计，应当按照减少资源消耗和废物产生的要求，优先选择采用易回收、易拆解、易降解、无毒无害或者低毒低害的材料和设计方案，并应当符合有关国家标准的强制性要求。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('231', '231', '判断题', '任何单位和个人不得将产生严重污染的生产设备转移给没有污染防治能力的单位使用。', '', '0', '0', '00010001', '0');
