INSERT INTO `lp_question` VALUES ('233', '233', '判断题', '跨行政区的环境污染和环境破坏的防治工作，由有关地方人民政府协商解决，或者由上级人民政府协调解决，作出决定。', '', '0', '0', '00010001', '0');
