INSERT INTO `lp_question` VALUES ('239', '239', '判断题', '管制是一种剥夺犯罪分子人身自由的刑罚。判处管制的犯罪分子，依法实行社区矫正。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('240', '240', '判断题', '《不动产登记暂行条例》规定，国家实行不动产统一登记制度。不动产登记遵循严格管理、稳定连续、方便群众的原则。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('241', '241', '判断题', '《不动产登记暂行条例》规定，查询不动产登记资料的单位、个人应当向不动产登记机构说明查询目的，不得将查询获得的不动产登记资料用于其他目的；未经权利人同意，不得泄露查询获得的不动产登记资料。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('242', '242', '判断题', '《不动产登记暂行条例》规定，伪造、变造不动产权属证书、不动产登记证明，或者买卖、使用伪造、变造的不动产权属证书、不动产登记证明的，由不动产登记机构或者公安机关依法予以收缴。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('243', '243', '判断题', '人民警察在执行上级的决定和命令过程中，认为决定和命令有错误的，可以按照规定提出意见，并可以中止或者改变决定和命令的执行。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('244', '244', '判断题', '人民警察对超越法律、法规规定的人民警察职责范围的指令，无权拒绝执行。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('245', '245', '判断题', '遇有拒捕、暴乱、越狱、抢夺枪支或者其他暴力行为的紧急情况，人民警察依照国家有关规定可以使用武器。', '', '0', '0', '00010001', '0');
