INSERT INTO `lp_question` VALUES ('247', '247', '判断题', '公民和组织因协助人民警察执行职务，造成人身伤亡或者财产损失的，应当按照国家有关规定给予抚恤或者补偿。', '', '0', '0', '00010001', '0');
INSERT INTO `lp_question` VALUES ('248', '248', '判断题', '人民警察遇到公民人身、财产安全受到侵犯或者处于其他危难情形，应当立即救助；对公民提出解决纠纷的要求，应当给予帮助；对公民的报警案件，应当及时查处。', '', '0', '0', '00010001', '0');
