INSERT INTO `lp_question` VALUES ('263', '263', '判断题', '根据《就业促进法》规定，职业中介机构根据需要可以扣押劳动者的居民身份证和其他证件，或者向劳动者收取押金。', '', '0', '0', '00010001', '0');
