INSERT INTO `lp_question` VALUES ('272', '272', '判断题', '监护人不履行监护职责或者侵害被监护人的合法利益的，应当承担责任；给被监护人造成财产损失的，应当赔偿损失。', '', '0', '0', '00010001', '0');
